member.example:
object[] items = null;
foreach(var item in items.NotNull()){
// result of items.NotNull() is empty but not null enumerable
}

object[] items = new object[]{ null, "Hello World!", null, "Good bye!" };
foreach(var item in items.NotNull()){
// result of items.NotNull() is enumerable with two strings
}

