var file = type.CreateInstance(@"c:\autoexec.bat");
if(file.GetPropertyValue<bool>("Exists")) {
var reader = file.InvokeMethod<StreamReader>("OpenText");
Console.WriteLine(reader.ReadToEnd());
reader.Close();
}

